import React, { Component } from 'react';
import kelpy from './kelpy.png';
import './App.css';

const roots = [0, 2, 4, 5, 7, 9, 11];
const notes = [0, 4, 7, 11];
const accidentals = [-1, 0, 1];
var givenRoot = Math.floor((Math.random() * 7));
var givenNote = Math.floor((Math.random() * 4));
var givenAccidental = Math.floor((Math.random() * 3));
var givenPitch = (roots[givenRoot] + notes[givenNote] + accidentals[givenAccidental])%12;
var givenRootString;
switch (givenRoot) {
  case 0:
  case 1:
  case 2:
  case 3:
  case 4:
  case 5:
  case 6:
}

function newNote() {
    givenRoot = Math.floor((Math.random() * 7));
    givenNote = Math.floor((Math.random() * 4));
    givenAccidental = Math.floor((Math.random() * 3));
    givenPitch = (roots[givenRoot] + notes[givenNote] + accidentals[givenAccidental])%12;
};

class App extends Component {
  noteClicked(){
    console.log(givenPitch);
  }
  accidentalClicked(){
    console.log('The accidental was clicked.');
  }
  checkClicked(){
    console.log('The check was clicked.');
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h4>
            The Music Theory App
          </h4>
          <img src={kelpy} className="kelpy" alt="it's kelpy"/>
        </header>
        <div className="App-body">
          <h1>
            [CDEFGAB]
          </h1>
          <h1>
            [I III V VII][♮ ♭ ♯]
          </h1>
          <h3>
            <button className="note" id="C" onClick={this.noteClicked}>C</button>
            <button className="note" id="D" onClick={this.noteClicked}>D</button>
            <button className="note" id="E" onClick={this.noteClicked}>E</button>
            <button className="note" id="F" onClick={this.noteClicked}>F</button>
            <button className="note" id="G" onClick={this.noteClicked}>G</button>
            <button className="note" id="A" onClick={this.noteClicked}>A</button>
            <button className="note" id="B" onClick={this.noteClicked}>B</button>
            <br></br>
            <br></br>
            <button className="accidental" id="flat" onClick={this.accidentalClicked}>b</button>
            <button className="accidental" id="sharp"  onClick={this.accidentalClicked}>#</button>
           </h3>
          <h2>
            <button className="check" id="check" onClick={this.checkClicked}>check</button>
          </h2>
          <h5 id="si">
            sí, correcto!
          </h5>
          <h5 id="no">
            no, incorrecto!
          </h5>
        </div>
      </div>
    );
  }
}

export default App;
