import React, { Component } from 'react';
import kelpy from './kelpy.png';
import './App.css';

const roots = [0, 2, 4, 5, 7, 9, 11];
const notes = [0, 4, 7, 11];
const accidentals = [-1, 0, 1];

var givenRoot;
var givenNote;
var givenAccidental;
var givenPitch;

var givenRootString;
var givenNoteString;
var givenAccidentalString;

var chosenPitch = -1;
var correct = -1;

class App extends Component {
  constructor(props) {
    super(props);
    init();
  }

  noteClicked(event, id) {
    console.log("note clicked!");
    note(id);
  }

  accidentalClicked(event, id) {
    console.log("accidental clicked!");
  }

  checkClicked(event, id) {
    console.log("check clicked!");
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h4>
            The Music Theory App
          </h4>
          <img src={kelpy} className="kelpy" alt="it's kelpy"/>
        </header>
        <div className="App-body">
          <h1 id="givenRoot">
            {givenRootString}
          </h1>
          <h1 id="givenNote">
            {givenNoteString}{givenAccidentalString}
          </h1>
          <h3>
            <button className="note" id="C" onClick={(e) => {this.noteClicked(e, "C")}}>C</button>
            <button className="note" id="D" onClick={(e) => {this.noteClicked(e, "D")}}>D</button>
            <button className="note" id="E" onClick={(e) => {this.noteClicked(e, "E")}}>E</button>
            <button className="note" id="F" onClick={(e) => {this.noteClicked(e, "F")}}>F</button>
            <button className="note" id="G" onClick={(e) => {this.noteClicked(e, "G")}}>G</button>
            <button className="note" id="A" onClick={(e) => {this.noteClicked(e, "A")}}>A</button>
            <button className="note" id="B" onClick={(e) => {this.noteClicked(e, "B")}}>B</button>
            <br></br>
            <br></br>
            <button className="accidental" id="flat" onClick={(e) => {this.accidentalClicked(e, "flat")}}>b</button>
            <button className="accidental" id="sharp"  onClick={(e) => {this.accidentalClicked(e, "sharp")}}>#</button>
           </h3>
          <h2>
            <button className="check" id="check" onClick={this.checkClicked}>check</button>
          </h2>
          <h5 id="si">
            sí, correcto!
          </h5>
          <h5 id="no">
            no, incorrecto!
          </h5>
        </div>
      </div>
    );
  }
}

function init(){
  givenRoot = Math.floor((Math.random() * 7));
  givenNote = Math.floor((Math.random() * 4));
  givenAccidental = Math.floor((Math.random() * 3));
  givenPitch = (roots[givenRoot] + notes[givenNote] + accidentals[givenAccidental])%12;
  newNote();
  toGivenStrings();
};

function toGivenStrings(){
  switch (givenRoot) {
    case 0:
      givenRootString = "C";
      break;
    case 1:
      givenRootString = "D";
      break;
    case 2:
      givenRootString = "E";
      break;
    case 3:
      givenRootString = "F";
      break;
    case 4:
      givenRootString = "G";
      break;
    case 5:
      givenRootString = "A";
      break;
    case 6:
      givenRootString = "B";
      break;
    default:
  }

  switch (givenNote) {
    case 0:
      givenNoteString = "I";
      break;
    case 1:
      givenNoteString = "III";
      break;
    case 2:
      givenNoteString = "IV";
      break;
    case 3:
      givenNoteString = "VII";
      break;
    default:
  }

  switch (givenAccidental) {
    case 0:
      givenAccidentalString = "♭";
      break;
    case 1:
      givenAccidentalString = "♮";
      break;
    case 2:
      givenAccidentalString = "♯";
      break;
    default:
  }
}

function newNote() {
    givenRoot = Math.floor((Math.random() * 7));
    givenNote = Math.floor((Math.random() * 4));
    givenAccidental = Math.floor((Math.random() * 3));
    givenPitch = (roots[givenRoot] + notes[givenNote] + accidentals[givenAccidental])%12;
};

function note(id) {
    var note;
    switch (id){
      case "C":
        note = 0;
        break;
      case "D":
        note = 2;
        break;
      case "E":
        note = 4;
        break;
      case "F":
        note = 5;
        break;
      case "G":
        note = 7;
        break;
      case "A":
        note = 9;
        break;
      case "B":
        note = 11;
        break;
      default:
    }
    chosenPitch = note;
    console.log(id);
  };

  function accidental(id){
    var accidental;
    switch (id){
      case "flat":
        accidental = -1;
        break;
      case "sharp":
        accidental = 1;
        break;
      default:
        accidental = 0;
    }
    chosenPitch += accidental;
    console.log(chosenPitch);
  };

  function acheckClicked(id){
    chosenPitch = chosenPitch%12;
    if (chosenPitch === givenPitch) {
      correct = 1;
    }
    else {
      correct = 0;
    }

    if (correct === 1) {
      init();
      console.log("sí, correcto!");
//      document.getElementById("si").innerHTML = "sí, correcto!";
    }
    else if (correct === 1) {
      console.log("no, incorrecto!");
//       document.getElementById("no").innerHTML = "no, incorrecto!";
    }
    else {
      console.log("waiting");
    }
  };

export default App;
