import React, { Component } from 'react';
import kelpy from './kelpy.png';
import './App.css';

class App extends Component {
  noteClick(){
    this.setState(state => ({
      isToggleOn: !state.isToggleOn
    }));
  }
  accidentalClick(){
    this.setState({isClicked : !this.state.isClicked});
  }
  checkClick(){
    this.setState({isClicked : !this.state.isClicked});
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h4>
            The Music Theory App
          </h4>
          <img src={kelpy} className="kelpy" alt="it's kelpy"/>
        </header>
        <body>
          <h1>
            [CDEFGAB]
          </h1>

          <h1>
            [I III V VII][♮ ♭ ♯]
          </h1>

          <h3>
            <button class="note" id="C" onClick={this.noteClicked}>{this.state.isToggleOn ? 'ON' : 'OFF'}C</button>
            <button class="note" id="D" onClick={this.noteClicked}>D</button>
            <button class="note" id="E" onClick={this.noteClicked}>E</button>
            <button class="note" id="F" onClick={this.noteClicked}>F</button>
            <button class="note" id="G" onClick={this.noteClicked}>G</button>
            <button class="note" id="A" onClick={this.noteClicked}>A</button>
            <button class="note" id="B" onClick={this.noteClicked}>B</button>
            <br></br>
            <br></br>
            <button class="accidental" id="flat" onClick={this.accidentalClicked}>b</button>
            <button class="accidental" id="sharp"  onClick={this.accidentalClicked}>#</button>
           </h3>

          <h2>
            <button class="check" id="check" onClick={this.checkClicked}>check</button>
          </h2>

          <h5 id="si">
            sí, correcto!
          </h5>

          <h5 id="no">
            no, incorrecto!
          </h5>
        </body>
      </div>
    );
  }
}

export default App;
