import React, { Component } from 'react';
import kelpy from './kelpy.png';
import './App.css';

const roots = [0, 2, 4, 5, 7, 9, 11];
const notes = [0, 4, 7, 11];
const accidentals = [-1, 0, 1];
var givenRoot = Math.floor((Math.random() * 7));
var givenNote = Math.floor((Math.random() * 4));
var givenAccidental = Math.floor((Math.random() * 3));
// eslint-disable-next-line
var givenPitch = (roots[givenRoot] + notes[givenNote] + accidentals[givenAccidental])%12;
var givenRootString;
var givenNoteString;
var givenAccidentalString;
toGivenStrings();
var chosenPitch = -1;
var correct = 0;

class App extends Component {



  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h4>
            The Music Theory App
          </h4>
          <img src={kelpy} className="kelpy" alt="it's kelpy"/>
        </header>
        <div className="App-body">
          <h1 id="givenRoot">
            [CDEFGAB]
          </h1>
          <h1 id="givenNote">
            [I III V VII][♮ ♭ ♯]
          </h1>
          <h3>
            <button className="note" id="C" onClick={noteClicked(this.id)}>C</button>
            <button className="note" id="D" onClick={this.noteClicked}>D</button>
            <button className="note" id="E" onClick={this.noteClicked}>E</button>
            <button className="note" id="F" onClick={this.noteClicked}>F</button>
            <button className="note" id="G" onClick={this.noteClicked}>G</button>
            <button className="note" id="A" onClick={this.noteClicked}>A</button>
            <button className="note" id="B" onClick={this.noteClicked}>B</button>
            <br></br>
            <br></br>
            <button className="accidental" id="flat" onClick={this.accidentalClicked}>b</button>
            <button className="accidental" id="sharp"  onClick={this.accidentalClicked}>#</button>
           </h3>
          <h2>
            <button className="check" id="check" onClick={this.checkClicked}>check</button>
          </h2>
          <h5 id="si">
            sí, correcto!
          </h5>
          <h5 id="no">
            no, incorrecto!
          </h5>
        </div>
      </div>
    );
  }
}

function toGivenStrings(){
  switch (givenRoot) {
    case 0:
      givenRootString = "C";
      break;
    case 1:
      givenRootString = "D";
      break;
    case 2:
      givenRootString = "E";
      break;
    case 3:
      givenRootString = "F";
      break;
    case 4:
      givenRootString = "G";
      break;
    case 5:
      givenRootString = "A";
      break;
    case 6:
      givenRootString = "B";
      break;
    default:
  }

  switch (givenNote) {
    case 0:
      givenNoteString = "I";
      break;
    case 1:
      givenNoteString = "III";
      break;
    case 2:
      givenNoteString = "IV";
      break;
    case 3:
      givenNoteString = "VII";
      break;
    default:
  }

  switch (givenAccidental) {
    case 0:
      givenAccidentalString = "♭";
      break;
    case 1:
      givenAccidentalString = "♮";
      break;
    case 2:
      givenAccidentalString = "♯";
      break;
    default:
  }
}

function newNote() {
    givenRoot = Math.floor((Math.random() * 7));
    givenNote = Math.floor((Math.random() * 4));
    givenAccidental = Math.floor((Math.random() * 3));
    givenPitch = (roots[givenRoot] + notes[givenNote] + accidentals[givenAccidental])%12;
};

function noteClicked(id) {
    var note;
    if (id) {
      switch (document.getElementById(id).innerHTML){
        case "C":
          note = 0;
          break;
        case "D":
          note = 2;
          break;
        case "E":
          note = 4;
          break;
        case "F":
          note = 5;
          break;
        case "G":
          note = 7;
          break;
        case "A":
          note = 9;
          break;
        case "B":
          note = 11;
          break;
        default:
      }
    }

    chosenPitch = note;
  };

  function accidentalClicked(id){
    var accidental;

    switch (document.getElementById(id).innerHTML){
      case "flat":
        accidental = -1;
        break;
      case "sharp":
        accidental = 1;
        break;
      default:
        accidental = 0;
    }

    chosenPitch += accidental;
  };

  function checkClicked(id){
    chosenPitch = chosenPitch%12;
    if (chosenPitch === givenPitch) {
      correct = 1;
    }
    else {
      correct = 0;
    }

    if (correct) {
      newNote();
      toGivenStrings();
      document.getElementById("givenRoot").innerHTML = givenRootString;
      document.getElementById("givenNote").innerHTML = givenNoteString + givenAccidentalString;
      document.getElementById("si").innerHTML = "sí, correcto!";
      document.getElementById("no").innerHTML = "";
    }
    else {
      document.getElementById("si").innerHTML = "";
      document.getElementById("no").innerHTML = "no, incorrecto!";
    }
  };

export default App;
